mapper.select:
SELECT
COUNT(1) AS TotalCount
FROM Wholesaler;

SELECT WholesalerID, Password, Cellphone, Email, Status, (CASE Status WHEN 'A' THEN '激活' WHEN 'F' THEN '冻结' END) AS StatusText, InUser, DATE_FORMAT(InDate, '%Y-%m-%d %H:%i:%s') AS InDate, LastEditUser, DATE_FORMAT(LastEditDate, '%Y-%m-%d %H:%i:%s') AS LastEditDate FROM Wholesaler WHERE 1 = 1
and Cellphone=#{arg2}
and Status=#{arg3}
ORDER BY InDate DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE Wholesaler SET
Password = #{password,jdbcType=NVARCHAR},
Cellphone = #{cellphone,jdbcType=NVARCHAR},
Email = #{email,jdbcType=NVARCHAR},
LastEditUser = #{lastEditUser,jdbcType=VARCHAR}
WHERE WholesalerID = #{wholesalerID,jdbcType=INTEGER}

UPDATE Wholesaler SET
Status = #{status,jdbcType=NVARCHAR},
LastEditUser = #{lastEditUser,jdbcType=VARCHAR}
WHERE WholesalerID = #{wholesalerID,jdbcType=INTEGER}

